export function splitPath(path) {
    return path
        .toString()
        .split(/[[\].]+/)
        .filter((p) => p);
}
export function mergePath(path) {
    return path.reduce((acc, next) => {
        if (typeof next === 'number' || !isNaN(parseInt(String(next), 10)))
            acc += `[${String(next)}]`;
        else if (!acc)
            acc += String(next);
        else
            acc += `.${String(next)}`;
        return acc;
    }, '');
}
